Ext.define('M_LOCAL_LC_MGM.controller.TbButtonController', {
    extend: 'Ext.app.Controller',
    stores: ['MyStore', 'MyStore1'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#delBtn": {
            click: 'delBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "mysearchform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        }
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var store1 = this.getMyStore1Store();
    	store.removeAll();
		store1.removeAll();
		
		var nows = new Date();
    	
        
		Ext.getCmp('V_LC_DOC_NO').setValue('');
		Ext.getCmp('V_XCH_RATE').setValue(1);
		Ext.getCmp('V_BANK_CD').setValue(null);
		Ext.getCmp('V_OPEN_DT').setValue(nows);
		nows.setMonth(nows.getMonth()+1);
		Ext.getCmp('V_EXPIRY_DT').setValue(nows);
		Ext.getCmp('V_AMEND_DT').setValue(new Date());
		Ext.getCmp('V_CUR').setValue('USD');
		
		
    	store.load({
    		params: {
    			V_TYPE: 'S',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			V_PO_NO: Ext.getCmp('V_PO_NO').getValue(),
    			V_PO_DT_FR: Ext.getCmp('V_PO_DT_FR').getValue(),
    			V_PO_DT_TO: Ext.getCmp('V_PO_DT_TO').getValue(),
    			V_M_BP_NM: Ext.getCmp('V_M_BP_NM').getValue()
    		},
    		callback: function(records,operations,success){
    		}
    	})
    },

    saveBtnClick: function(button, e, eOpts) {
        alert('save');
    },

    delBtnClick: function(button, e, eOpts) {
		
		var store = Ext.getStore('MyStore');
		var store1 = Ext.getStore('MyStore1');
		var flag = '';
		var msg = '';
		
		Ext.Msg.confirm('확인', '일괄삭제하시겠습니까?', function(button) {
			if (button == 'yes') {


				var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
				var gridRecord1 = Ext.getCmp('myGrid1').getSelectionModel().getSelection();

				
				for(var i=0; i<gridRecord.length; i++) {
					if(gridRecord[i].get('V_TYPE') == 'V') {
						gridRecord[i].set('V_TYPE', 'SD');
					}
				}
				
				store.sync({
					params : {
						V_TYPE : 'SYNC',
						V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
						V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
						V_LC_DOC_NO : Ext.getCmp('V_LC_DOC_NO').getValue(),
						V_BANK_CD : Ext.getCmp('V_BANK_CD').getValue(),
						V_XCH_RATE : Ext.getCmp('V_XCH_RATE').getValue(),
						V_OPEN_DT : Ext.getCmp('V_OPEN_DT').getValue(),
						V_EXPIRY_DT : Ext.getCmp('V_EXPIRY_DT').getValue(),
						V_AMEND_DT : Ext.getCmp('V_AMEND_DT').getValue(),
						V_CUR : Ext.getCmp('V_CUR').getValue(),
					},
					callback : function(records, operation, success) {
						store.reload();
						store1.removeAll();
					}, 
					success: function() {}
				});
			}
		});
	},

    clsBtnClick: function(button, e, eOpts) {
        alert('close');
    },

    tfEnter: function(field, e, eOpts) {
        	if (e.getKey() == e.ENTER) {
        		this.selBtnClick();
        	}
    }

});
