Ext.define('Q_RESULT_MGM.controller.TbButtonController', {
    extend: 'Ext.app.Controller',
    stores: [
        'MyStore'
    ],

    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#saveBtn1": {
        	click: 'saveBtn1Click'
        },
        "#delBtn": {
            click: 'delBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "#clsBtn1": {
        	click: 'clsBtn1Click'
        },
        "mysearchform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        }
    },

    selBtnClick: function(button, e, eOpts) {
//        alert('select');
    	var store = this.getMyStoreStore();
    	store.removeAll();
    	store.load({
    		params: {
    			V_TYPE: 'S',
    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
                V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
                V_ASN_NO : Ext.getCmp('V_ASN_NO').getValue(),
                V_BP_CD : Ext.getCmp('V_M_BP_CD').getValue(),
                V_DL_FR_DT : Ext.getCmp('V_DL_FR_DT').getValue(),
                V_DL_TO_DT : Ext.getCmp('V_DL_TO_DT').getValue(),
                V_ITEM_CD : Ext.getCmp('V_ITEM_CD').getValue(),
                V_INSP_DT : Ext.getCmp('V_INSP_DT').getValue(),
                
    		},
    		callback: function(records,operations,success){
    		}
    	})
    },

    saveBtn1Click: function(button, e, eOpts) {
//        alert('save');
    	var store = Ext.getCmp('myGrid').getStore();
    	Ext.Msg.confirm('확인','저장하시겠습니까?', function(button){
			if(button == 'yes') {
				Ext.Ajax.request({
					url: 'sql/q_result_mgm_hspf.jsp',
					method: 'post',
					params: {
						V_TYPE: 'U',
						V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
						V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
						V_OK_QTY : Ext.getCmp('W_OK_QTY').getValue(),
						V_BAD_QTY : Ext.getCmp('W_BAD_QTY').getValue(),
						V_INSP_USR_ID : Ext.getCmp('W_INSP_USR_ID').getValue(),
						V_INSP_DT : Ext.getCmp('W_INSP_DT').getValue(),
						V_Q_RS_NO : Ext.getCmp('W_Q_RS_NO').getValue(),
					},
					success : function(response) {
						store.reload();
					}
				});
			}
    	});
    },

    delBtnClick: function(button, e, eOpts) {
        alert('delete');
    },

    clsBtnClick: function(button, e, eOpts) {
    	var tab=parent.Ext.getCmp('myTab');
        var activeTab=tab.getActiveTab();
        var tabIndex=tab.items.indexOf(activeTab);
        tab.remove(tabIndex,true);
    },

    tfEnter: function(field, e, eOpts) {
        	if (e.getKey() == e.ENTER) {
        		this.selBtnClick();
        	}
    },
    
    clsBtn1Click: function(button, e, eOpts) {
    	Ext.getCmp('popup').destroy();
    }
    
});
