Ext.define('S_ASN_FILE.controller.TbButtonController', {
    extend: 'Ext.app.Controller',
    stores: ['MyStore'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "#saveBtn": {
        	click: 'saveBtnClick'
        },
        "textfield[name=search_field]": {
            specialkey: 'tfEnter'
        },
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	
    	store.load({
    		params: {
    			V_TYPE: 'S',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_S_BP_NM: Ext.getCmp('V_S_BP_NM').getValue(),
    			V_FILE_DT_FR: Ext.getCmp('V_FILE_DT_FR').getValue(),
    			V_FILE_DT_TO: Ext.getCmp('V_FILE_DT_TO').getValue(),
    		},
    		callback: function(records,operations,success){
		    	
    		}
    	});
    },
    saveBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
    	var fileArr = new Array();
    	
    	for(var i=0; i < gridRecord.length; i++) {
    		fileArr.push(gridRecord[i].get('FILE_IN_SYSTEM_NM'));
    	}

    	
    	function removeDuplicatesArray(arr) {
            var tempArr = [];
            for (var i = 0; i < arr.length; i++) {
                if (tempArr.length == 0) {
                    tempArr.push(arr[i]);
                } else {
                    var duplicatesFlag = true;
                    for (var j = 0; j < tempArr.length; j++) {
                        if (tempArr[j] == arr[i]) {
                            duplicatesFlag = false;
                            break;
                        }
                    }
                    if (duplicatesFlag) {
                        tempArr.push(arr[i]);
                    }
                }
            }
            return tempArr;
        }
    	
    	Ext.Ajax.request({
			url:'sql/S_ASN_FILE.jsp',
			method: 'post',
			params: {
				V_TYPE : 'FILE',
				V_ARR: removeDuplicatesArray(fileArr)
			},
			callback : function(records,operations,success){
			},
			success : function(response) {
				
				var fileName = encodeURI(response.responseText);
				
				var myWin=new Ext.Window(
						{
							title : '파일다운로드',
							html : '<iframe name="xxx" border =0 src="sql/S_ASN_FILE.jsp?V_TYPE=FD&V_FILE_NM='+fileName+'" width="1%" height="1%"></iframe>',
							width :500,
							height:500
						});
				myWin.show();
				myWin.hide();
				
			},
			scope: this
		});
    	
    	
    },

    

    delBtnClick: function(button, e, eOpts) {
        alert('delete');
    },

    clsBtnClick: function(button, e, eOpts) {
        alert('close');
    },

    tfEnter: function(field, e, eOpts) {
    	if (e.getKey() == e.ENTER) {
    		this.selBtnClick();
    	}
    }
});
