Ext.define('S_DN_BC_HSPF.controller.TbButtonController', {
    extend: 'Ext.app.Controller',

    stores: [
        'MyStore'
    ],

    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#delBtn": {
            click: 'delBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "mysearchform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        }
    },

    selBtnClick: function(button, e, eOpts) {
        //alert('select');
    	if(Ext.getCmp('V_BP_NM').getValue() == '' || Ext.getCmp('V_BP_NM').getValue() == null){
    		Ext.Msg.alert('확인', '출고처를 입력해주세요.')
    	}
    	else if(Ext.getCmp('V_TO_SL_CD').getValue() == '' || Ext.getCmp('V_TO_SL_CD').getValue() == null){
    		Ext.Msg.alert('확인', 'TO창고를 입력해주세요.')
    	}
    	
    	else{
    		var store = this.getMyStoreStore();
        	store.proxy.setTimeout(60000);
        	store.removeAll();
        	store.load({
        		params: {
        			V_TYPE: 'S',
        			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
    				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
                    V_S_BP_CD : Ext.getCmp('V_BP_CD').getValue(),
                    V_S_BP_NM : Ext.getCmp('V_BP_NM').getValue(),
                    V_DN_DT_FROM : Ext.getCmp('V_DN_DT_FROM').getValue(),
                    V_DN_DT_TO : Ext.getCmp('V_DN_DT_TO').getValue(),
                    V_TO_SL_CD : Ext.getCmp('V_TO_SL_CD').getValue(),
                    V_DD_NO : Ext.getCmp('V_DD_NO').getValue(),
                    V_ITEM_CD : Ext.getCmp('V_ITEM_CD').getValue(),
                    V_ITEM_NM : Ext.getCmp('V_ITEM_NM').getValue(),
                    V_BC_NO : Ext.getCmp('V_BC_NO').getValue(),

        		},
        		callback: function(records,operations,success){
        		}
        	});
    	}
    	
    },

    saveBtnClick: function(button, e, eOpts) {
        alert('save');
    },

    delBtnClick: function(button, e, eOpts) {
        alert('delete');
    },

    clsBtnClick: function(button, e, eOpts) {
    	var tab=parent.Ext.getCmp('myTab');
        var activeTab=tab.getActiveTab();
        var tabIndex=tab.items.indexOf(activeTab);
        tab.remove(tabIndex,true);
    },

    tfEnter: function(field, e, eOpts) {
        	if (e.getKey() == e.ENTER) {
        		this.selBtnClick();
        	}
    }

});
