Ext.define('S_DN_HSPF.controller.TbButtonController', {
    extend: 'Ext.app.Controller',
    stores: ['MyStore', 'MyStore1', 'MyStore2'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#delBtn": {
            click: 'delBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "mysearchform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        }
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var store1 = this.getMyStore1Store();
    	var store2 = this.getMyStore2Store();
    	
    	store.removeAll();
    	store1.removeAll();
    	store2.removeAll();
    	//좌측출고요청정보조회
    	store.load({
    		params: {
    			V_TYPE: 'SH',
    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_DR_DT_FR: Ext.getCmp('V_DR_DT_FR').getValue(),
				V_DR_DT_TO: Ext.getCmp('V_DR_DT_TO').getValue(),
				V_BP_CD: Ext.getCmp('V_BP_CD').getValue(),
				V_BP_NM: Ext.getCmp('V_BP_NM').getValue(),
    		},
    		callback: function(records,operations,success){
    			Ext.toast({
					  title: ' ',
					  timeout: 1000,
					  html: '조회완료',
					  style: 'text-align: center',
					  align: 't',
					  width: 130,
				});
    		}
    	});
    	
		//우측출고정보조회
		store2.load({
			params: {
				V_TYPE: 'DS',
				V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
				V_BP_CD: Ext.getCmp('V_BP_CD').getValue(),
				V_BP_NM: Ext.getCmp('V_BP_NM').getValue(),
				V_DN_DT_FROM: Ext.getCmp('V_DN_DT_FROM').getValue(),
				V_DN_DT_TO: Ext.getCmp('V_DN_DT_TO').getValue(),
			},
			callback: function(records,operations,success){
			}
		});
    },

    saveBtnClick: function(button, e, eOpts) {
        alert('save');
    },

    delBtnClick: function(button, e, eOpts) {
        alert('delete');
    },

    clsBtnClick: function(button, e, eOpts) {
        var tab=parent.Ext.getCmp('myTab');
                 var activeTab=tab.getActiveTab();
                 var tabIndex=tab.items.indexOf(activeTab);
                 tab.remove(tabIndex,true);
    },

    tfEnter: function(field, e, eOpts) {
        	if (e.getKey() == e.ENTER) {
        		this.selBtnClick();
        	}
    }

});
